var (
	str           = ""
	finalResFloat float64
	finalResInt   int64
)

func BenchmarkMul1(b *testing.B) {
	var res float64
	for i := 0; i < b.N; i++ {
		res = mul1.Mul(str, 13)
	}
	finalResFloat = res
}

